tinymce.PluginManager.add('close', function(editor, url) {
  // Add a button that opens a window
  editor.addButton('close', {
    text: 'Close',
    icon: false,
    onclick: close
  });

  editor.addMenuItem('close', {
    text: 'Close',
    context: 'file',
    onclick: close,
  });

  function close(){
    console.log($('#' + editor.id));
    $('#' + editor.id).parent().remove();
    $('.wrapper').show();
  }
});
